// Guarda notificación cuando una luminaria presenta 3 sobreconsumos consecutivos
// Usa context para almacenar contadores temporales

let luminarias = msg.payload.luminarias || [];
if (!Array.isArray(luminarias) || luminarias.length === 0) return null;

const now = new Date();
const notificaciones = [];

luminarias.forEach(l => {
    const id = l.luminariaID || l.id_luminaria;
    const sobreconsumo = l.sobreconsumo === true || l.overcurrent === true;

    // Obtener contador previo del contexto
    let contador = context.get(id) || 0;

    if (sobreconsumo) {
        contador++;
        context.set(id, contador);

        // Si llegó a 3, generar notificación
        if (contador >= 3) {
            const notificacion = {
                id_notificacion: new Date().getTime().toString(),
                id_luminaria: id,
                fecha: now.toISOString(),
                descripcion: `⚠️ Luminaria ${id} en sobreconsumo por mucho tiempo`
            };
            notificaciones.push(notificacion);

            // Reiniciar contador después de registrar
            context.set(id, 0);
        }
    } else {
        // Reinicia si ya no hay sobreconsumo
        context.set(id, 0);
    }
});

if (notificaciones.length > 0) {
    msg.payload = notificaciones;
    return msg;
} else {
    return null; // No enviar nada si no hay alertas
}

// detectar luminarias en bajo consumo consecutivo
let luminarias = msg.payload.luminarias || [];
if (!Array.isArray(luminarias) || luminarias.length === 0) return null;

const now = new Date();
const alertas = [];

luminarias.forEach(l => {
    const id = l.luminariaID || l.id_luminaria;
    const bajo = (l.W !== undefined && l.W < 10) || (l.consumo !== undefined && l.consumo < 0.1); // umbral configurable

    let contador = context.get(id) || 0;

    if (bajo) {
        contador++;
        context.set(id, contador);

        if (contador >= 3) {
            alertas.push({ id_luminaria: id, fecha: now.toISOString(), descripcion: `⚠️ Luminaria ${id} en bajo consumo por mucho tiempo` });
            context.set(id, 0);
        }
    } else {
        context.set(id, 0);
    }
});

if (alertas.length > 0) {
    msg.payload = { alertas };
    return [msg, null]; // sale por la primera salida para notificación
}

