try {
    const validacion_fechas = (fecha) => {
        const fecha_actual = new Date();

        if (!fecha || isNaN(new Date(fecha).getTime())) {
            return fecha_actual;
        }

        fecha = new Date(fecha);

        let day = fecha.getDate();
        let month = fecha.getMonth();
        let year = fecha.getFullYear();

        let curr_day = fecha_actual.getDate();
        let curr_month = fecha_actual.getMonth();
        let curr_year = fecha_actual.getFullYear();

        if (fecha < new Date('2025-01-01')) {
            if (curr_day > day) fecha.setDate(curr_day);
            if (curr_month > month) fecha.setMonth(curr_month);
            if (curr_year > year) fecha.setFullYear(curr_year);
        } else if (fecha > fecha_actual) {
            if (curr_day < day) fecha.setDate(curr_day);
            if (curr_month < month) fecha.setMonth(curr_month);
            if (curr_year < year) fecha.setFullYear(curr_year);
        }

        return fecha;
    };

    data_mediciones = data_mediciones.map(
        (x) => {
            x.fecha = validacion_fechas(x.fecha);
            return x
        }
    );

